import React, { useState, useEffect } from "react";
import "./App.css";
import axios from "axios";

function App() {
  const [meetings, setMeetings] = useState([]);
  const [title, setTitle] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [flag, setFlag] = useState(true);

  const handleSubmit = async (e) => {
    setFlag(!flag);
    e.preventDefault();
    console.log(title, startTime, endTime, "yyyyyyyyyyyy");
    try {
      const response = await axios.post("http://localhost:5000/api/meetings", {
        title,
        startTime,
        endTime,
      });
      setMeetings([...meetings, response.data]);
      setTitle("");
      setStartTime("");
      setEndTime("");
    } catch (error) {
      console.error("Error creating meeting:", error);
    }
  };

  useEffect(() => {
    fetchMeetings();
  }, [flag]);

  const fetchMeetings = async () => {
    try {
      console.log("isideee");
      const response = await axios.get("http://localhost:5000/api/meetings");
      setMeetings(response.data);
    } catch (error) {
      console.log("isideeelllllll");
      console.error("Error fetching meetings:", error);
    }
  };

  return (
    <div className="main">
      <h1>Scheduled Meetings</h1>
      <form onSubmit={handleSubmit}>
        <div className="formInputs">
          <div style={{ display: "flex", flexDirection: "column" }}>
            <label>Title</label>
            <input
              type="text"
              placeholder="Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
              className="inputs"
            />
          </div>
          <div style={{ display: "flex", flexDirection: "column" }}>
            <label>Start Time</label>
            <input
              type="datetime-local"
              placeholder="Start Time"
              value={startTime}
              onChange={(e) => setStartTime(e.target.value)}
              required
              className="inputs"
            />
          </div>
          <div style={{ display: "flex", flexDirection: "column" }}>
            <label>End Time</label>
            <input
              type="datetime-local"
              placeholder="End Time"
              value={endTime}
              onChange={(e) => setEndTime(e.target.value)}
              required
              className="inputs"
            />
          </div>
        </div>
        <div>
          <button type="submit" className="submitButton">
            Schedule Meeting
          </button>
        </div>
      </form>

        <ul>
          {meetings.map((meeting) => (
            <div className="meeting-details">
            <li key={meeting.id}>
              <div>Title: {meeting.title}</div>
              <div>Start Time: {meeting.startTime}</div>
              <div>End Time: {meeting.endTime}</div>
              <div>
                Link:{" "}
                <a
                  href={meeting.link}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  {meeting.link}
                </a>
              </div>
            </li>
            </div>
          ))}
        </ul>

    </div>
  );
}

export default App;
