import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [meetings, setMeetings] = useState([]);
  const [title, setTitle] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');

  useEffect(() => {
    fetchMeetings();
  }, []);

  const fetchMeetings = async () => {
    try {
      const response = await axios.get('/api/meetings');
      setMeetings(response.data);
    } catch (error) {
      console.error('Error fetching meetings:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log(title,
      startTime,
      endTime,"yyyyyyyyyyyy")
    try {
      const response = await axios.post('/api/meetings', {
        title,
        startTime,
        endTime
      });
      setMeetings([...meetings, response.data]);
      setTitle('');
      setStartTime('');
      setEndTime('');
    } catch (error) {
      console.error('Error creating meeting:', error);
    }
  };

  return (
    <div>
      <h1>Scheduled Meetings</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} />
        <input type="datetime-local" placeholder="Start Time" value={startTime} onChange={(e) => setStartTime(e.target.value)} />
        <input type="datetime-local" placeholder="End Time" value={endTime} onChange={(e) => setEndTime(e.target.value)} />
        <button type="submit">Schedule Meeting</button>
      </form>
      <ul>
        {meetings.map(meeting => (
          <li key={meeting.id}>
            <div>Title: {meeting.title}</div>
            <div>Start Time: {meeting.startTime}</div>
            <div>End Time: {meeting.endTime}</div>
            <div>Link: <a href={meeting.link} target="_blank" rel="noopener noreferrer">{meeting.link}</a></div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
